import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Switch, Link } from 'react-router-dom';
import Home from './Home';
import CompanyList from './CompanyList';
import CompanyDetail from './CompanyDetail';
import JobList from './JobList';
import JobDetail from './JobDetail';
import LoginForm from './LoginForm';
import SignupForm from './SignupForm';
import Profile from './Profile';
import JoblyApi from './api';
import { getToken, setToken } from './localStorage'; // Import localStorage utility
import ProtectedRoute from './ProtectedRoute'; // Import ProtectedRoute

const App = () => {
  const [currentUser, setCurrentUser] = useState(null);
  const [token, setTokenState] = useState(getToken()); // Initialize token from localStorage

  useEffect(() => {
    const loadUser = async () => {
      if (token) {
        const userData = await JoblyApi.getUserProfile(currentUser.username); // Fetch user data
        setCurrentUser(userData);
      }
    };
    loadUser();
  }, [token]);

  const login = async (username, password) => {
    const { token } = await JoblyApi.login(username, password);
    setTokenState(token);
    setToken(token); // Store token in localStorage
    JoblyApi.token = token; // Store token in API class
    setCurrentUser({ username }); // Set current user
  };

  const signup = async (username, password) => {
    const { token } = await JoblyApi.signup(username, password);
    setTokenState(token);
    setToken(token); // Store token in localStorage
    JoblyApi.token = token; // Store token in API class
    setCurrentUser({ username }); // Set current user
  };

  const logout = () => {
    setTokenState(null);
    setCurrentUser(null);
    setToken(null); // Clear token from localStorage
    JoblyApi.token = null; // Clear token in API class
  };

  return (
    <Router>
      <nav>
        <ul>
          <li><Link to="/">Home</Link></li>
          <li><Link to="/companies">Companies</Link></li>
          <li><Link to="/jobs">Jobs</Link></li>
          {currentUser ? (
            <>
              <li>{currentUser.username}</li>
              <li><button onClick={logout}>Log Out</button></li>
            </>
          ) : (
            <>
              <li><Link to="/login">Login</Link></li>
              <li><Link to="/signup">Sign Up</Link></li>
            </>
          )}
        </ul>
      </nav>
      <Switch>
        <Route exact path="/" component={Home} />
        <ProtectedRoute exact path="/companies" component={CompanyList} isAuthenticated={!!token} />
        <ProtectedRoute path="/companies/:handle" component={CompanyDetail} isAuthenticated={!!token} />
        <ProtectedRoute exact path="/jobs">
          <JobList currentUser={currentUser} />
        </ProtectedRoute>
        <ProtectedRoute path="/jobs/:id">
          <JobDetail currentUser={currentUser} />
        </ProtectedRoute>
        <Route path="/login">
          <LoginForm login={login} />
        </Route>
        <Route path="/signup">
          <SignupForm signup={signup} />
        </Route>
        <ProtectedRoute path="/profile">
          <Profile currentUser={currentUser} setCurrentUser={setCurrentUser} />
        </ProtectedRoute>
      </Switch>
    </Router>
  );
};

export default App;
